closeclearr = 0.013;L = 0.278;l = 0.1;h = -0.05;tau=0:0.01:1;s = 3*tau.^2 - 2*tau.^3;x0 = 0.18;y0 = l/2;X = x0+h*sin(pi*s);Y = y0 - l*s;plot(s, X, s, Y)numPoints = length(s); % Define the number of points for the rangeZ = zeros(1,numPoints);for i=1:numPoints        [~,z] = task2length([X(i); Y(i)], r, L);        Z(i) = z;    endfigure(1)plot3(X, Y, Z, '*k')grid onaxis imagexlabel xylabel y;zlabel z;title('3D Plot of HTM Outputs');% save("gait_trajectory.mat", "X", "Y", "Z");